import React, { useContext, useState } from 'react';
import './userprofile.css';
import { UserContext } from '../context/UserContext'; // Import UserContext

const Userprofile = () => {
  const { user } = useContext(UserContext); // Get user from context
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState(user);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = () => {
    // Save logic can be implemented here
    setIsEditing(false);
  };

  const handleLogout = () => {
    // Implement logout logic here, e.g., clearing tokens, redirecting to login page
    alert('Logged out');
  };

  return (
    <div className="profile-container">
      <h1>User Profile</h1>
      <div className="profile-details">
        {isEditing ? (
          <>
            <div className="profile-field">
              <label>Username:</label>
              <input type="text" name="username" value={formData?.username || ''} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Email:</label>
              <input type="email" name="email" value={formData?.email || ''} onChange={handleChange} />
            </div>
            <button onClick={handleSave} className="profile-button">Save</button>
          </>
        ) : (
          <>
            <div className="profile-field">
              <label>Username: </label>
              <span>{user?.username}</span>
            </div>
            <div className="profile-field">
              <label>Email: </label>
              <span>{user?.email}</span>
            </div>
            <button onClick={handleEdit} className="profile-button">Edit</button>
          </>
        )}
      </div>
      <button onClick={handleLogout} className="profile-button logout-button">Logout</button>
    </div>
  );
};

export default Userprofile;
