import React, { useState } from 'react';
import './userprofile.css';
// import { Link } from 'react-router-dom';

const Userprofile = () => {
  // Dummy user data; replace with actual user data fetching logic
  const [user, setUser] = useState({
    Username: 'ram',
    Email: 'ram@gmail.com',
    
  });

  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState(user);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = () => {
    setUser(formData);
    setIsEditing(false);
  };

  const handleLogout = () => {
    // Implement logout logic here, e.g., clearing tokens, redirecting to login page
    alert('Logged out');
  };

  return (
    <div className="profile-container">
      <h1>User Profile</h1>
      <div className="profile-details">
        {isEditing ? (
          <>
            <div className="profile-field">
              <label>User Name: ram </label>
              <input type="text" name="name" value={formData.name} onChange={handleChange}
              />
            </div>
            <div className="profile-field">
              <label>Email:  ram@gamil.com</label>
              <input type="email" name="email"  value={formData.email} onChange={handleChange}  />
            </div>
            {/* <div className="profile-field">
              <label>Phone:</label>
              <input type="tel" name="phone" value={formData.phone} onChange={handleChange} />
            </div>
            <div className="profile-field">
              <label>Address:</label>
              <input type="text" name="address" value={formData.address} onChange={handleChange} />
            </div>
            <button onClick={handleSave} className="profile-button">Save</button> */}
          </>
        ) : (
          <>
            <div className="profile-field">
              <label>User&nbsp;Name:&nbsp;ram</label>
              <span>{user.name}</span>
            </div>
            <div className="profile-field">
              <label>Email:&nbsp;ram@gamil.com</label>
              <span>{user.email}</span>
            </div>
            {/* <div className="profile-field">
              <label>Phone:</label>
              <span>{user.phone}</span>
            </div>
            <div className="profile-field">
              <label>Address:</label>
              <span>{user.address}</span>
            </div>
            <button onClick={handleEdit} className="profile-button">Edit</button> */}
          </>
        )}
      </div>
      <button onClick={handleLogout} className="profile-button logout-button">Logout</button>
    </div>
  );
};

export default Userprofile;
