import React, { useState } from 'react';
import './Login.css';
import axios from 'axios'; // Import axios for making HTTP requests

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  // Function to handle input changes
  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  // Function to handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Make a POST request to your Express backend
      const response = await axios.post('/signinsubmit', formData);
      // Assuming your backend returns a success message upon successful signup
      alert(response.data.message); // Show a success message
      // Redirect user to home page or wherever you want
    } catch (error) {
      console.error('Error signing up:', error);
      alert('Something went wrong. Please try again.'); // Show an error message
    }
  };

  return (
    <div className="signup-body">
      <h1>Sign Up</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Username:</label>
          <input type="text" id="username" name="username" value={formData.username} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}

export default Signup;
