import React, { useState, useEffect } from 'react';
import './kanchi.css'; // Importing CSS file for styles

const Kanchi = () => {
  const [hotels, setHotels] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/travelers.json'); // Adjust the path as needed
        const data = await response.json();
        setHotels(data.hotels);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div id="middle">
      {/* Your slideshow and content here */}
      <div className="bottom">
        <h1 className="place_name">Kanchi Hotels</h1>
        <ul className="hotels-container">
          {hotels.map((hotel, index) => (
            <li key={index} className="hotel">
              <div id="name">
                <h3>{hotel.name}</h3>
              </div>
              <img src={hotel.img} alt={`${hotel.name} Image`} width="300" height="200" />
              <p><label>Location: </label>{hotel.location}</p>
              <p><label>Type: </label>{hotel.type || 'Hotel'}</p>
              <p><label>Rating: </label>{hotel.rating}</p>
              <p><label>Price: </label>â‚¹{hotel.price}</p>
              <a href="http://localhost:3003/contact"><button className="btn">Book Now</button></a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Kanchi;
