import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';
import { Link } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Dummy authentication logic; replace with actual API call
    if (email === 'test@example.com' && password === 'password') {
      navigate('/userprofile');
    } else {
      alert('Invalid credentials');
    }
  };

  return (
    <div className='body'>
      <div className="login_top">
        <h1>Login... </h1>
        <form onSubmit={handleSubmit}>
          <div className="login_form">
            <div className="login_f1">
              <label className='login_label' htmlFor="email">Mail&nbsp;ID</label>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <input
                className="login_input"
                type="email"
                name="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="login_f1">
              <label className='login_label' htmlFor="password">Password</label>&nbsp;&nbsp;&nbsp;
              <input
                className="login_input"
                type="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div>
              <button type="submit" className="login_btnn">Submit</button>
            </div>
          </div>
        </form>
        <Link to="/signup">SignUp</Link>
      </div>
      <p className="login_logo_body">
        {/* <img src="https://cdn.glitch.global/74966f89-6b4a-4631-ad79-95f11a65e0dd/logo11.jpg?v=1697535569992" alt="" /> */}
      </p>
    </div>
  );
}

export default Login;
