// app.js (or your server file)

const express = require("express");
const app = express();
const port = 3003;

// Import Firebase Admin SDK components
const { initializeApp, cert } = require("firebase-admin/app");
const { getFirestore, Timestamp, FieldValue } = require("firebase-admin/firestore");
const serviceAccount = require("./serviceAccountKey.json"); // Your service account key file

// Initialize Firebase Admin SDK
initializeApp({
  credential: cert(serviceAccount),
});

// Get a Firestore instance
const db = getFirestore();

// Other imports and middleware...

app.post("/signinsubmit", (req, res) => {
  const Username = req.body.username;
  const Email = req.body.email;
  const Password = req.body.password;

  // Check if email already exists
  db.collection("ExploreEpic")
    .where("Email", "==", Email)
    .get()
    .then((emailDocs) => {
      if (!emailDocs.empty) {
        res.send("Hey, an account with this email address already exists.");
      } else {
        // Check if username already exists
        db.collection("ExploreEpic")
          .where("UserName", "==", Username)
          .get()
          .then((userDocs) => {
            if (!userDocs.empty) {
              res.send("Hey, an account with this username already exists.");
            } else {
              // If email and username are unique, add the new user to Firestore
              db.collection("ExploreEpic")
                .add({
                  UserName: Username,
                  Email: Email,
                  Password: Password, // Note: You may want to hash the password before storing it in a production environment
                  // Optionally, you can add more user data here
                  CreatedAt: FieldValue.serverTimestamp(), // Add a timestamp for when the account was created
                })
                .then(() => {
                  res.render("signin_after", { UserName: Username });
                })
                .catch((error) => {
                  console.error("Error adding user to Firestore:", error);
                  res.send("Something went wrong while creating the account.");
                });
            }
          })
          .catch((error) => {
            console.error("Error checking username in Firestore:", error);
            res.send("Something went wrong while checking the username.");
          });
      }
    })
    .catch((error) => {
      console.error("Error checking email in Firestore:", error);
      res.send("Something went wrong while checking the email.");
    });
});

// Other routes and app.listen...




