// src/components/Signup.js
import React from 'react';
import './Signup.css';

function Signup() {
  // Function to handle form submission
  const handleSubmit = (event) => {
    // Prevent the default form submission behavior
    event.preventDefault();
    // Perform form submission logic here...
    // If signup is successful, redirect to the home page
    window.location.href = "/home"; // Redirect to the home page
  };

  return (
    <div className="signup-page">
      <div className="top">
        <h1>Signin...</h1>
        <form onSubmit={handleSubmit}>
          <div className="form">
            <div className="f1">
              <label htmlFor="username">User Name</label>
              <input type="text" name="username" />
            </div>
            <div className="f1">
              <label htmlFor="email">Mail Id</label>
              <input type="email" name="email" />
            </div>
            <div className="f1">
              <label htmlFor="password">Password</label>
              <input type="password" name="password" />
            </div>
            <div>
              <input type="checkbox" className="check" />I accept all terms & conditions
            </div>
            <div>
              <button type="submit" className="btnn">Submit</button>
            </div>
          </div>
        </form>
      </div>
      <p className="logo_body">
        <img src="https://cdn.glitch.global/74966f89-6b4a-4631-ad79-95f11a65e0dd/logo11.jpg?v=1697535569992" alt="Helping Hands Logo" />
      </p>
    </div>
  );
}

export default Signup;
