import React from 'react';
import './nearby.css';
import data from '../travelers.json'; // Adjust the path according to the location of travelers.json

const Nearby = () => {
  return (
    <center>
      <div className="container_top">
        <center>
          <div className="content_container_top">
            "Traveling to nearby places is like turning the page of a thrilling adventure novel 
            every chapter reveals new experiences and exciting surprises."
          </div>
        </center>
        <div className="container">
          <center>
            <div className="content_container">
              <p>Top 9 BEST places to visit near TIRUPATI</p>
            </div>
          </center>
          {data.chikkamagaluru && data.chikkamagaluru.map((place, index) => (
            <a href={`http://localhost:3003/${place.name.toLowerCase().replace(/\s+/g, '')}`} key={index}>
              <div className="card1" title={`To know more about ${place.name}... just click on it`}>
                <div>
                  <img src={place.image} alt={place.name} className="card-image1" />
                </div>
                <div className="card-content1">
                  <h2 className="card-title">{place.name}</h2>
                  <p className="card-description p">{place.description}</p>
                </div>
              </div>
            </a>
          ))}
        </div>
        {/* Add more sections for other destinations similarly */}
      </div>
    </center>
  );
};

export default Nearby;
